import R from 'ramda';

import products from './mockProducts';
import categories from './mockCategories';

// this js file is for fetching data from our local data storage api

export const fetchProducts = async () => {
    return new Promise(resolve => {
        resolve(products);
    });
};

//pass offset so react knows to fetch products that havn't been fetched yet
//not using offset here as dummy data is not large enough
export const loadMoreProducts = async (/*{offset}*/) => {
    return new Promise(resolve => {
        resolve(products);
    });
};

export const fetchProductById = async id => {
    return new Promise((resolve, reject) => {
        const product = R.find(R.propEq('id', id), products);
        resolve(product);
    });
};

export const fetchCategories = async() => {
    return new Promise(resolve => {
        resolve(categories);
    });
};