import R from 'ramda';

import { 
    ADD_PRODUCT_TO_BASKET, 
    REMOVE_PRODUCT_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes';

// this reducer is for state of basket

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch(type) {
    case ADD_PRODUCT_TO_BASKET:
        // return new list with payload added to current state
        return R.append(payload, state);
    case REMOVE_PRODUCT_FROM_BASKET:
        // return new list with payload removed from current state
        return R.without(R.of(payload), state);
    case CLEAN_BASKET:
        return [];
    default:
        return state;
    }
};