import R from 'ramda';

import {
    FETCH_CATEGORIES_SUCCESS
} from 'actionTypes';

// this reducer is for state of products' categories

const initialState = {
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
    case FETCH_CATEGORIES_SUCCESS:
        //use id as key
        const newValues = R.indexBy(R.prop('id'), payload);
        //new values are added or override duplicate existing data and return new obj
        return R.merge(state, newValues);
    default:
        return state;
    }
};